CREATE OR ALTER PROCEDURE [dbo].[THEM_MAU] @TEN_MAU NVARCHAR(50),@TEN_TIENG_ANH VARCHAR(20), @MA_NV VARCHAR(15)
AS
BEGIN
	DECLARE @errorMessage NVARCHAR(500) = N''
	DECLARE @responseMessage NVARCHAR(500) = N''
