		IF EXISTS(SELECT * FROM BANG_MAU WHERE TEN_MAU = @TEN_MAU COLLATE SQL_Latin1_General_CP1_CI_AS)
		BEGIN
			SET @errorMessage = N'Tên màu đã tồn tại'
