CREATE OR ALTER FUNCTION [dbo].[UDF_TRI_GIA_CUA_PT]
(
    @MA_PT VARCHAR(15)
)
RETURNS INT
AS
BEGIN
    DECLARE @result INT = 0;
   
        SET @result =
        (
            SELECT SUM (ctpt.SO_LUONG * ctgh.GIA) 
			FROM dbo.CHI_TIET_GIO_HANG ctgh 
			INNER JOIN (SELECT gh.ID_GH, ctpt1.SO_LUONG, ctpt1.MA_CT_SP FROM CHI_TIET_PHIEU_TRA ctpt1 INNER JOIN PHIEU_TRA pt ON 
