CREATE OR ALTER FUNCTION [dbo].[UDF_TONG_SL_MUA_CUA_SP_TRONG_N_THANG_GAN_NHAT]
(
    @MA_SP VARCHAR(15),
	@N INT
)
RETURNS INT
AS
BEGIN
    DECLARE @result INT = 0;
	DECLARE @d DATETIME = DATEADD(MONTH,-@N, GETDATE())
        SET @result =
        (
            SELECT SUM (SO_LUONG) FROM dbo.CHI_TIET_GIO_HANG 
			INNER JOIN (SELECT * FROM GIO_HANG WHERE GIO_HANG.TRANG_THAI <> -1 ) GIO_HANG
			ON GIO_HANG.ID_GH = CHI_TIET_GIO_HANG.ID_GH
			INNER JOIN CHI_TIET_SAN_PHAM ON CHI_TIET_GIO_HANG.MA_CT_SP = CHI_TIET_SAN_PHAM.MA_CT_SP
			WHERE CHI_TIET_SAN_PHAM.MA_SP = @MA_SP AND (MONTH(NGAY_TAO) >= MONTH(@d) AND (YEAR(NGAY_TAO) >= YEAR(@d)))
        );
    RETURN @result;
END;

