CREATE OR ALTER PROCEDURE [dbo].[LAY_SP_BAN_CHAY_TRONG_N_THANG] @top INT = NULL, @n INT = NULL
AS
BEGIN
	IF(@top IS NULL)
	SET @top = (SELECT COUNT(*) FROM dbo.SAN_PHAM)

