				, dbo.UDF_LayTongSoLuongTonCuaMotSP(S.MA_SP) AS TONG_SL_TON 
				, dbo.[UDF_TONG_SL_MUA_CUA_SP_TRONG_N_THANG_GAN_NHAT](S.MA_SP, @n) AS TONG_SL_DA_BAN
				FROM dbo.SAN_PHAM S
