			BEGIN
				SET @errorMessage = N'Có lỗi xảy ra khi xóa hình ảnh của sản phẩm'
				GOTO ABORT
			END
		DELETE CHI_TIET_SAN_PHAM WHERE MA_SP = @MA_SP
		IF @@ERROR <> 0
			BEGIN
				SET @errorMessage = N'Có lỗi xảy ra khi xóa chi tiết của sản phẩm'
